
public class Factwhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1, fact=1;
		System.out.println("the factorial numbers from 1 to 10 are:  ");
		while(i<=10)
		{
			fact=fact*i;
			System.out.println(" "+fact);
			i++;
		}
		}
}
